pub fn find_the_difference(s: String, t: String) -> char {
    let mut schars: Vec<char> = s.chars().collect();
    schars.sort();
    let mut schars = schars.into_iter();
    let mut tchars: Vec<char> = t.chars().collect();
    tchars.sort();
    let mut tchars = tchars.into_iter();

    for _i in 0..t.len() {
        let char1 = schars.next();
        let char2 = tchars.next();
        if char1 != char2 {
            return char2.unwrap();
        }
    }
    ' '
}

#[cfg(test)]
mod test {
    use crate::find_the_difference;

    #[test]
    fn ex1() {
        assert_eq!(
            find_the_difference("abcd".to_string(), "abcde".to_string()),
            'e'
        )
    }

    #[test]
    fn ex2() {
        assert_eq!(
            find_the_difference("".to_string(), "".to_string()),
            't'
        )
    }
}
